import { Component } from '@angular/core';

@Component({
  selector: 'app-second',
  templateUrl: './second.component.html',
  styleUrls: ['./second.component.css']
})
export class SecondComponent {
  pictureNumber = 1;
  myInterval:any;

  next() {
    if(this.pictureNumber != 6) {
      this.pictureNumber++;
    }
  }

  prev() {
    if(this.pictureNumber != 1) {
      this.pictureNumber--;
    }
  }

  slide() {
    this.myInterval = setInterval(() => {
      if(this.pictureNumber != 6) {
        this.pictureNumber++;
      }else{
        this.pictureNumber = 1;
      }
    }, 500);
  }

  stop() {
    clearInterval(this.myInterval);
  }

}
<!-- <div class="card bg-dark text-white"> -->
<img
  src="/assets/images/{{ pictureNumber }}.jpg"
  class="card-img object-fit-cover"
  alt="..."
  height="500px"
/>
<!-- </div> -->

<button class="btn btn-primary me-2 my-2" (click)="next()">Next</button>
<button class="btn btn-primary me-2 my-2" (click)="prev()">Prev</button>
<button class="btn btn-primary me-2 my-2" (click)="slide()">Slide Show</button>
<button class="btn btn-primary me-2 my-2" (click)="stop()">Stop</button>
