import { Component, OnInit } from '@angular/core';
import { Product } from '../Interfaces/Product';
import {Observable, map, interval, takeUntil, switchMap} from 'rxjs';
import {Products} from '../Services/products.service'

@Component({
  selector: 'app-second',
  templateUrl: './second.component.html',
  styleUrls: ['./second.component.css']
})
export class SecondComponent implements OnInit {

  constructor(private productService: Products) {}

  count: number = 0;

  products$: Observable<any> = new Observable();
  products: [] = [];

  ngOnInit() : void {
    // this.products$ = this.productService.getAllProducts().pipe(map(response => response.products));

    this.products$ = interval(1000).pipe(
      switchMap(() => this.productService.getAllProducts()),
      map(response => response.products)
    );


}

}

<div class="mb-3">
  <label for="exampleInputEmail1" class="form-label"
    >Enter number of products</label
  >
  <input
    type="text"
    class="form-control"
    id="exampleInputEmail1"
    aria-describedby="emailHelp"
    [(ngModel)]="count"
  />
</div>

<div *ngIf="products$ | async as products">
  <table class="table">
    <thead>
      <tr>
        <th scope="col">Product</th>
        <th scope="col">Code</th>
        <th scope="col">Category</th>
        <th scope="col">Price</th>
        <th scope="col">In Stock</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products.slice(0, count)">
        <!-- <th scope="row">1</th> -->
        <td>{{ product.title }}</td>
        <td>{{ product.id }}</td>
        <td>{{ product.category }}</td>
        <td>{{ product.price }}</td>
        <td>{{ product.stock }}</td>
      </tr>
    </tbody>
  </table>
</div>
